sysauto_script.script:
var howLongtoClose = gs.getProperty(x_159938_needit.autoCloseOverdue);
var overdueNITasks = new GlideRecord('x_159938_needit_needit_task');
overdueNITasks.addQuery('due_date','<=',gs.daysAgo(howLongtoClose));
overdueNITasks.addQuery('state','<',3);
overdueNITasks.query();
while(overdueNITasks.next()){
	gs.info("The Task number is "+ overdueNITasks.number);
	overdueNITasks.work_notes = "Task auto closed because the this task is open for too long period";
	overdueNITasks.state = 4;
	overdueNITasks.update();
}
